import { useState } from 'react';
import { CreditCard, Wallet, ChevronRight, Lock, CheckCircle, AlertCircle } from 'lucide-react';

export default function Payment() {
  const [paymentMethod, setPaymentMethod] = useState('upi');
  const [upiId, setUpiId] = useState('');
  const [cardNumber, setCardNumber] = useState('');
  const [cardName, setCardName] = useState('');
  const [cardExpiry, setCardExpiry] = useState('');
  const [cardCvv, setCvv] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const [paymentStatus, setPaymentStatus] = useState(null);

  const handleSubmit = (e) => {
    if (e) e.preventDefault();
    setIsProcessing(true);

    // Simulate payment processing
    setTimeout(() => {
      setIsProcessing(false);
      setPaymentStatus('success');
    }, 2000);
  };

  const formatCardNumber = (value) => {
    const v = value.replace(/\s+/g, '').replace(/[^0-9]/gi, '');
    const matches = v.match(/\d{4,16}/g);
    const match = matches && matches[0] || '';
    const parts = [];

    for (let i = 0; i < match.length; i += 4) {
      parts.push(match.substring(i, i + 4));
    }

    if (parts.length) {
      return parts.join(' ');
    } else {
      return value;
    }
  };

  const handleCardNumberChange = (e) => {
    const formatted = formatCardNumber(e.target.value);
    setCardNumber(formatted);
  };

  const handleExpiryChange = (e) => {
    let value = e.target.value;
    value = value.replace(/\D/g, '');

    if (value.length > 2) {
      value = value.substring(0, 2) + '/' + value.substring(2, 4);
    }

    setCardExpiry(value);
  };

  return (
    <div className="flex flex-col p-6 max-w-md mx-auto bg-white rounded-lg shadow-md">
      <h1 className="text-2xl font-bold mb-6 text-black">Complete Your Payment</h1>

      {paymentStatus === 'success' ? (
        <div className="flex flex-col items-center justify-center py-8">
          <CheckCircle size={64} className="text-green-500 mb-4" />
          <h2 className="text-xl font-semibold mb-2">Payment Successful!</h2>
          <p className="text-gray-600 mb-6 text-center">Your order has been placed successfully.</p>
          <div className="border-t border-gray-200 w-full pt-4">
            <button 
              onClick={() => window.location.href = '/home'}
              className="w-full bg-black text-white py-3 rounded-lg font-medium">
              Continue Shopping
            </button>
          </div>
        </div>
      ) : (
        <>
          <div className="flex flex-col mb-6">
            <p className="text-sm text-gray-600 mb-2">Amount to pay</p>
            <p className="text-3xl font-bold">₹2,450.00</p>
            <div className="flex items-center mt-1">
              <span className="text-sm text-gray-500">Order #AGR78245</span>
              <span className="mx-2 text-gray-300">•</span>
              <span className="text-sm text-gray-500">20kg Premium Rice</span>
            </div>
          </div>

          <div className="mb-6">
            <p className="font-medium mb-3">Payment Method</p>
            <div className="grid grid-cols-3 gap-2">
              <button 
                className={`flex flex-col items-center justify-center py-3 border rounded-lg ${paymentMethod === 'upi' ? 'border-2 border-black' : 'border-gray-200'}`}
                onClick={() => setPaymentMethod('upi')}
              >
                <Wallet size={20} className="mb-1" />
                <span className="text-sm">UPI</span>
              </button>
              <button 
                className={`flex flex-col items-center justify-center py-3 border rounded-lg ${paymentMethod === 'card' ? 'border-2 border-black' : 'border-gray-200'}`}
                onClick={() => setPaymentMethod('card')}
              >
                <CreditCard size={20} className="mb-1" />
                <span className="text-sm">Card</span>
              </button>
              <button 
                className={`flex flex-col items-center justify-center py-3 border rounded-lg ${paymentMethod === 'cod' ? 'border-2 border-black' : 'border-gray-200'}`}
                onClick={() => setPaymentMethod('cod')}
              >
                <Wallet size={20} className="mb-1" />
                <span className="text-sm">Cash</span>
              </button>
            </div>
          </div>

          <div>
            {paymentMethod === 'upi' && (
              <div className="mb-6">
                <label className="block mb-2 text-sm font-medium">
                  UPI ID
                </label>
                <div className="relative">
                  <input
                    type="text"
                    value={upiId}
                    onChange={(e) => setUpiId(e.target.value)}
                    placeholder="yourname@upi"
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-1 focus:ring-black focus:border-black"
                  />
                  <div className="absolute inset-y-0 right-0 flex items-center pr-3 pointer-events-none">
                    <ChevronRight size={16} className="text-gray-400" />
                  </div>
                </div>
                <div className="mt-2 flex items-center text-xs text-gray-500">
                  <span>Popular options: PhonePe, GooglePay, Paytm, BHIM</span>
                </div>
              </div>
            )}

            {paymentMethod === 'card' && (
              <div className="space-y-4">
                <div>
                  <label className="block mb-2 text-sm font-medium">
                    Card Number
                  </label>
                  <input
                    type="text"
                    value={cardNumber}
                    onChange={handleCardNumberChange}
                    placeholder="1234 5678 9012 3456"
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-1 focus:ring-black focus:border-black"
                    maxLength="19"
                  />
                </div>

                <div>
                  <label className="block mb-2 text-sm font-medium">
                    Cardholder Name
                  </label>
                  <input
                    type="text"
                    value={cardName}
                    onChange={(e) => setCardName(e.target.value)}
                    placeholder="Name on card"
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-1 focus:ring-black focus:border-black"
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block mb-2 text-sm font-medium">
                      Expiry Date
                    </label>
                    <input
                      type="text"
                      value={cardExpiry}
                      onChange={handleExpiryChange}
                      placeholder="MM/YY"
                      className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-1 focus:ring-black focus:border-black"
                      maxLength="5"
                    />
                  </div>
                  <div>
                    <label className="block mb-2 text-sm font-medium">
                      CVV
                    </label>
                    <input
                      type="password"
                      value={cardCvv}
                      onChange={(e) => setCvv(e.target.value)}
                      placeholder="123"
                      className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-1 focus:ring-black focus:border-black"
                      maxLength="3"
                    />
                  </div>
                </div>
              </div>
            )}

            {paymentMethod === 'cod' && (
              <div className="mb-6 p-4 bg-gray-50 rounded-lg">
                <div className="flex items-start">
                  <AlertCircle size={20} className="mr-3 mt-0.5 text-gray-500" />
                  <div>
                    <p className="font-medium">Cash on Delivery</p>
                    <p className="text-sm text-gray-600 mt-1">
                      Pay in cash at the time of delivery. Please keep exact change.
                    </p>
                  </div>
                </div>
              </div>
            )}

            <div className="mt-6">
              <button
                onClick={handleSubmit}
                className="w-full bg-black hover:bg-gray-800 text-white font-medium py-3 px-4 rounded-lg transition flex justify-center items-center"
                disabled={isProcessing}
              >
                {isProcessing ? (
                  <span className="flex items-center">
                    <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                    </svg>
                    Processing...
                  </span>
                ) : (
                  <span className="flex items-center">
                    Pay ₹2,450.00 <Lock size={16} className="ml-2" />
                  </span>
                )}
              </button>

              <div className="mt-6 flex items-center justify-center text-xs text-gray-500">
                <Lock size={14} className="mr-1" />
                <span>Secure payment processing by HarvestPay</span>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
}
