import React, { useState } from 'react';

const initialCart = [
  { id: 1, name: 'Grains & Seeds', price: 20, quantity: 1 },
  { id: 2, name: 'Agricultural Tools', price: 50, quantity: 2 }
];

export default function CartPage() {
  const [cartItems, setCartItems] = useState(initialCart);

  const updateQuantity = (id, quantity) => {
    setCartItems(prev =>
      prev.map(item =>
        item.id === id ? { ...item, quantity: Number(quantity) } : item
      )
    );
  };

  const removeItem = id => {
    setCartItems(prev => prev.filter(item => item.id !== id));
  };

  const total = cartItems.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Your Cart</h2>

      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          <div style={styles.cartList}>
            {cartItems.map(item => (
              <div key={item.id} style={styles.cartItem}>
                <div style={styles.itemDetails}>
                  <h3>{item.name}</h3>
                  <p>${item.price} each</p>
                </div>
                <input
                  type="number"
                  min="1"
                  value={item.quantity}
                  onChange={e => updateQuantity(item.id, e.target.value)}
                  style={styles.quantityInput}
                />
                <button onClick={() => removeItem(item.id)} style={styles.removeBtn}>
                  Remove
                </button>
              </div>
            ))}
          </div>

          <div style={styles.totalSection}>
            <h3>Total: ${total.toFixed(2)}</h3>
            <button style={styles.checkoutBtn}>Proceed to Checkout</button>
          </div>
        </>
      )}
    </div>
  );
}

const styles = {
  container: {
    maxWidth: '800px',
    margin: '2rem auto',
    padding: '1rem',
    fontFamily: 'Arial, sans-serif',
    backgroundImage: 'url("./components/cartbg.")', // Change this to your actual path
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    borderRadius: '12px',
    boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
    color: '#fff' // Optional: ensure text remains readable
  },
  heading: {
    fontSize: '2rem',
    marginBottom: '1rem',
    textAlign: 'center'
  },
  cartList: {
    display: 'flex',
    flexDirection: 'column',
    gap: '1rem'
  },
  cartItem: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '1rem',
    border: '1px solid #ccc',
    borderRadius: '8px'
  },
  itemDetails: {
    flex: 2
  },
  quantityInput: {
    width: '60px',
    padding: '0.25rem',
    textAlign: 'center',
    marginRight: '1rem'
  },
  removeBtn: {
    backgroundColor: '#E53E3E',
    color: 'white',
    border: 'none',
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    cursor: 'pointer'
  },
  totalSection: {
    marginTop: '2rem',
    textAlign: 'right'
  },
  checkoutBtn: {
    marginTop: '1rem',
    padding: '0.75rem 1.5rem',
    backgroundColor: '#2F855A',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    fontSize: '1rem',
    cursor: 'pointer'
  }
};
