import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Footer from './Footer';
import { Link } from 'react-router-dom';

const HomePage = () => {
  const navigate = useNavigate();
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [activeCategory, setActiveCategory] = useState('All');

  // Product categories with sample products
  const categories = [
    {
      name: 'Seeds',
      products: [
        { 
          id: 1, 
          name: 'Organic Tomato Seeds', 
          price: '₹120/packet', 
          description: 'High-yield hybrid variety suitable for various soil types.',
          image: '/images/tomatoSeeds.png' 
        },
        { 
          id: 2, 
          name: 'Premium Wheat Seeds', 
          price: '₹1800/kg', 
          description: 'Drought-resistant wheat variety with excellent germination rate.',
          image: '/images/wheatSeeds.png' 
        }
      ]
    },
    {
      name: 'Fertilizers',
      products: [
        { 
          id: 3, 
          name: 'Organic Compost', 
          price: '₹450/bag', 
          description: 'Nutrient-rich natural compost for all soil types.',
          image: '/images/organic-compost.jpg' 
        },
        { 
          id: 4, 
          name: 'NPK Fertilizer', 
          price: '₹750/bag', 
          description: 'Balanced NPK formula for healthy plant growth and development.',
          image: '/images/npk-fertilizer.jpg' 
        }
      ]
    },
    {
      name: 'Tools',
      products: [
        { 
          id: 5, 
          name: 'Garden Trowel Set', 
          price: '₹350', 
          description: 'Durable stainless steel set for precision gardening tasks.',
          image: '/images/trowel-set.jpg' 
        },
        { 
          id: 6, 
          name: 'Pruning Shears', 
          price: '₹280', 
          description: 'Sharp and ergonomic design for efficient pruning.',
          image: '/images/pruning-shears.jpg' 
        }
      ]
    },
    {
      name: 'Equipment',
      products: [
        { 
          id: 7, 
          name: 'Mini Tiller', 
          price: '₹12,500', 
          description: 'Compact and powerful tiller for small to medium gardens.',
          image: '/images/mini-tiller.jpg' 
        },
        { 
          id: 8, 
          name: 'Garden Sprayer', 
          price: '₹870', 
          description: 'Adjustable pressure sprayer for fertilizers and pesticides.',
          image: '/images/garden-sprayer.jpg' 
        }
      ]
    }
  ];

  // Toggle side drawer
  const toggleDrawer = () => {
    setDrawerOpen(!drawerOpen);
  };

  // Filter products based on active category
  const filteredProducts = activeCategory === 'All' 
    ? categories.flatMap(category => category.products)
    : categories.find(category => category.name === activeCategory)?.products || [];

  return (
    <div className="homepage">
      {/* Navigation Bar */}
      <nav className="navbar">
        <div className="navbar-left">
          <button className="menu-button" onClick={toggleDrawer}>
            <div className="menu-icon">
              <span></span>
              <span></span>
              <span></span>
            </div>
          </button>
          <h1>AgroCart</h1>
        </div>
        <div className="navbar-right">
          <ul className="nav-links">
            <li><a href="#home" className="active">Home</a></li>
            <li><a href="#products">Products</a></li>
            <li><Link to="/About">About</Link></li>
            <li><a href="#contact">Contact</a></li>
          </ul>
          <div className="nav-buttons">
            <button className="cart-button" onClick={() => navigate('/cart')}>
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M9 22C9.55228 22 10 21.5523 10 21C10 20.4477 9.55228 20 9 20C8.44772 20 8 20.4477 8 21C8 21.5523 8.44772 22 9 22Z" stroke="white" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M20 22C20.5523 22 21 21.5523 21 21C21 20.4477 20.5523 20 20 20C19.4477 20 19 20.4477 19 21C19 21.5523 19.4477 22 20 22Z" stroke="white" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M1 1H5L7.68 14.39C7.77144 14.8504 8.02191 15.264 8.38755 15.5583C8.75318 15.8526 9.2107 16.009 9.68 16H19.4C19.8693 16.009 20.3268 15.8526 20.6925 15.5583C21.0581 15.264 21.3086 14.8504 21.4 14.39L23 6H6" stroke="white" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
              <span className="cart-count">2</span>
            </button>
            <button className="auth-button">Login</button>
          </div>
        </div>
      </nav>

      {/* Side Drawer Navigation */}
      <div className={`side-drawer ${drawerOpen ? 'open' : ''}`}>
        <div className="drawer-header">
          <div className="user-info">
            <div className="user-avatar">
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M20 21V19C20 17.9391 19.5786 16.9217 18.8284 16.1716C18.0783 15.4214 17.0609 15 16 15H8C6.93913 15 5.92172 15.4214 5.17157 16.1716C4.42143 16.9217 4 17.9391 4 19V21" stroke="white" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M12 11C14.2091 11 16 9.20914 16 7C16 4.79086 14.2091 3 12 3C9.79086 3 8 4.79086 8 7C8 9.20914 9.79086 11 12 11Z" stroke="white" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </div>
            <p>Welcome, Guest</p>
          </div>
          <button className="close-drawer" onClick={toggleDrawer}>×</button>
        </div>
        <div className="drawer-content">
          <div className="drawer-section">
            <h3>Categories</h3>
            <ul className="drawer-categories">
              <li onClick={() => {setActiveCategory('All'); toggleDrawer();}}>All Products</li>
              {categories.map((category, index) => (
                <li key={index} onClick={() => {setActiveCategory(category.name); toggleDrawer();}}>
                  {category.name}
                </li>
              ))}
            </ul>
          </div>
          <div className="drawer-section">
            <h3>Account</h3>
            <ul className="drawer-links">
              <li>Profile</li>
              <li>Orders</li>
              <li>Wishlist</li>
              <li>Settings</li>
            </ul>
          </div>
          <div className="drawer-section">
            <h3>Help</h3>
            <ul className="drawer-links">
              <li>FAQs</li>
              <li>Contact Support</li>
              <li>About Us</li>
            </ul>
          </div>
        </div>
      </div>

      {/* Main Content Area */}
      <div className="main-content">
        <div className="hero-section" style={{ backgroundImage: "url('/images/hero-background.jpg')", backgroundSize: 'cover', backgroundPosition: 'center' }}>
          <div className="hero-text">
            <h1>Welcome to AgroCart</h1>
            <p>Your one-stop destination for all farming essentials.</p>
            <button className="explore-button" onClick={() => navigate('/products')}>Explore Products</button>
          </div>
        </div>

        <div className="product-section">
          <h2>Our Products</h2>
          <div className="product-categories">
            <button onClick={() => setActiveCategory('All')}>All</button>
            {categories.map((category, index) => (
              <button key={index} onClick={() => setActiveCategory(category.name)}>{category.name}</button>
            ))}
          </div>
          <div className="product-list">
            {filteredProducts.map(product => (
              <div key={product.id} className="product-card">
                <img src={product.image} alt={product.name} />
                <h3>{product.name}</h3>
                <p>{product.description}</p>
                <p className="price">{product.price}</p>
                <button className="view-details" onClick={() => navigate(`/product/${product.id}`)}>View Details</button>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Footer */}
      <Footer />
    </div>
  );
};

export default HomePage;
